scriptlet.script:
//var ipaddr = "10.1.2.3";
var maskLength = parseInt(maskLength, 10);
//var gateway = parseInt("1", 10);


//console.log("maskLength:" + maskLength);

var strMaskDot = ""
var intMask = ""
for (var i = 0; i < 4; i++) {
    var intDigit = 0
    for (var j = 0; j < 8; j++) {
        if ((j + (i * 8)) < maskLength) {
            strMaskDot += "1";
            intDigit += Math.pow(2 , (8 - j - 1));
            //console.log("intDigit:" + intDigit);
        } 
        else strMaskDot += "0";
    };
    intMask += intDigit.toString()
    if (i < 3) {
        strMaskDot += "."
        intMask += "."
    }

};

//console.log("strMaskDot:" + strMaskDot);
//console.log("intMask:" + intMask);
//console.log("ipaddr:" + ipaddr);

scriptletResult=intMask
