scriptlet.script:
function randPassword(lettersUpp,lettersLow,numbers,especial, either) {
  var characters = [
   "ABCDEFGHIJKLMNOPQRSTUVWXYZ",
   "0123456789",
   "abcdefghijklmnopqrstuvwxyz",
   "+-*._#",
   "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789"
  ];
  //var c1=x[Math.floor(Math.random() * x.length)]
  var result="";
  for ( var i = 0; i < lettersUpp; i++ ) {
    result += characters[0].charAt(Math.floor(Math.random() * characters[0].length));
  }
  for ( var i = 0; i < lettersLow; i++ ) {
    result += characters[1].charAt(Math.floor(Math.random() * characters[1].length));
  }
  for ( var i = 0; i < numbers; i++ ) {
    result += characters[2].charAt(Math.floor(Math.random() * characters[2].length));
  }
  for ( var i = 0; i < especial; i++ ) {
    result += characters[3].charAt(Math.floor(Math.random() * characters[3].length));
  }
  for ( var i = 0; i < either; i++ ) {
    var alphabet=characters[0]+characters[1]+characters[2]+characters[3];

    result += alphabet.charAt(Math.floor(Math.random() * alphabet.length));
  }

  return shuffle(result);

}

function shuffle (strCadena) {
    var a = strCadena.split(""),
        n = a.length;

    for(var i = 0 ; i < n ; i++) {
        var j = Math.floor(Math.random() * (n));
        var tmp = a[i];
        a[i] = a[j];
        a[j] = tmp;
    }
    return a.join("");
}
